

def ulify(elements, parentClass = '', childClass = ''):
    string = f'<ul class="{parentClass}">\n'
    for s in elements:
        string += f'<li class="{childClass}">\n<span>' + iconify(s[-4:]) + str(s) + '</span>' + f'<a href="./public/{s}" download>' + iconify('download') + '</a>\n</li>\n'
    string += '</ul>\n'
    return string


def nestedUlify(elements, parentClass = '', childClass = ''):
    string = f'<div class="{parentClass}">\n'
    for e in elements:
        string += f'<div class="{childClass}">\n'
        parentDir = list(e.items())[0][0]
        string += f'<div class="controls">\n<p>{iconify("folder")} {parentDir}</p>\n'
        string += f'<button class="accordion">\n{iconify("folder-open")}</button>\n</div>'
        string += '<ul class="panel">\n'
        for li in list(e.items())[0][1]:
            string += '<li>\n<span>' +iconify(li[-4:]) + li + '</span>' + f'<a href="./public/{parentDir}/{li}" download>\n{iconify("download")}</a>\n</li>'
        string += '</ul>\n'
        string += '</div>\n'
    string += '</div>\n'
    return string


